export const nftCollectionByteCode = '';


export const deployContract = async (name: string, symbol: string) => {
  const args = [name, symbol];
  const currentAccounts = await web3.eth.getAccounts();
  const params = {from: currentAccounts[0]};

  // deploy contract
  const contract = new web3.eth.Contract(nftCollectionAbi as unknown as AbiItem, '0x0000000000000000000000000000000000000000');
  const deployTx = contract.deploy({data: nftCollectionByteCode, arguments: args});

  const deployResult = await deployTx.send(params);
  console.log({deployResult});
  return deployResult;
};
